import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import './Jobpost.css';

class JobForm extends Component 
{
    constructor() 
    {
        super()
        this.state = 
        {
            data: [],
        }
    }

    render() {
        return(
            <div className='form-body'>
                <div className='form-heading'> Post Job </div>

                <form>

                    <div className='sub-heading'> 
                        Basic Details
                    </div>

                    <div class="form-group">
                        <label for="job_title">
                            Job Title <span className='required'>*</span> 
                        </label>
                        <input type="text" className="form-control" id="job_title" refs="job_title" placeholder="Write a title that appropriatly describes the job.">
                        </input>
                    </div>

                    <div class="form-group">
                        <label for="exampleInputEmail1">
                            Location <span className='required'>*</span> 
                        </label>
                        <input type="text" className="form-control" refs="job_title" placeholder="Write a title that appropriatly describes the job.">
                        </input>
                    </div>


                    <button type="submit" class="btn btn-primary">Submit</button>

                </form>
            </div>  
        )
    }
}

export default JobForm;
