import React, {  useState  } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Typeahead } from 'react-bootstrap-typeahead';
import ReactTagInput from "@pathofdev/react-tag-input";
import { useForm } from "react-hook-form";

import options from './locations_data';
import category_list from './category_data';
import functional_area_list from './functional_area_data';

import './Jobpost.css';
import 'react-bootstrap-typeahead/css/Typeahead.css';
import "@pathofdev/react-tag-input/build/index.css";

function TagsInPut() {
  const [tags, setTags] = React.useState([])
  return (
    <ReactTagInput 
      tags={tags} 
      placeholder="Type and press enter"
      maxTags={10}
      editable={true}
      readOnly={false}
      removeOnBackspace={true}
      onChange={(newTags) => setTags(newTags)}

    />
  )
}

const LocationSelection = () => {
  const [selected, setSelected] = [];

  return (
    <Typeahead
      multiple  
      id="locations"
      onChange={setSelected}
      options={options}
      placeholder="Please add locations here..."
      selected={selected}
    />
  );
};

const CategorySelection = () => {
  const [selected, setSelected] = [];

  return (
    <Typeahead
      id="category_list"
      onChange={setSelected}
      options={category_list}
      placeholder="Please Choose Category"
      selected={selected}
    />
  );
};

const FunctionalAreaSelection = () => {
  const [selected, setSelected] = [];

  return (
    <Typeahead
      id="functional_area_list"
      onChange={setSelected}
      options={category_list}
      placeholder="Please Choose Functional Area"
      selected={selected}
    />
  );
};

//class JobForm extends Component 
function JobForm()
{
    const [year_data, full_year_data] = useState([]);

    const {register, handleSubmit} = useForm();
    const onSubmit = data => console.log(data);

    // constructor() 
    // {
    //     //super()
    //     this.state = 
    //     {
    //         year_data: [],
    //         full_year_data: [],
    //         fields: {
    //             "jobtitle": "",
    //             "minexp": "",
    //             "maxexp": "",
    //             "jobdescription": "",
    //             "jobcategory": "",
    //             "jobarea": "",
    //             "graduateminyear": "",
    //             "graduatemaxyear": "",
    //             "locations": "locations"
    //         },
    //         errors: {}
    //     }
    // }

    setYearData: {

        var array=[];

        for (var i = 1; i <=15; i++) {
            array.push(i);
        }

        var f_year_data = [];
        var current_year   = new Date().getFullYear();

        for (var i = 2000; i <=current_year; i++) {
            f_year_data.push(i);
        }

        // year_data = array;
        // full_year_data = f_year_data;
        // this.setState({
        //                 year_data : array,
        //                 full_year_data : full_year_data
        //             })

    }

    componentDidMount: {
        this.setYearData()
    }



    //render() {
        return(
            <div className='form-body'>
                <div className='form-heading'> Post Job </div>

                <form name="jobpostform" onSubmit={handleSubmit(onSubmit)}>

                    <div className='sub-heading'> 
                        Basic Details
                    </div>

                    <div className="form-group">
                        <label for="job_title">
                            Job Title <span className='required'>*</span> 
                        </label>
                        <input type="text" ref={register} className="form-control" id="job_title" refs="job_title" placeholder="Write a title that appropriatly describes the job.">
                        </input>
                    </div>

                    <div className="form-group">
                        <label for="job_locatiion">
                            Locations <span className='required'>*</span> 
                        </label>
                        <LocationSelection />
                    </div>

                    <div className="form-group">
                        <label>
                            Years of Experience <span className='required'>*</span> 
                        </label>
                          <div className="row">
                            <div className="col">
                                <select className="form-control" ref={register}>
                                    <option selected>Select Min</option>
                                    {this.state.year_data.map(d => (<option value={d}>{d}</option>))}
                                </select>
                            </div>
                            <div className="col">
                                <select className="form-control" ref={register}>
                                    <option selected>Select Max</option>
                                    {this.state.year_data.map(d => (<option value={d}>{d}</option>))}
                                </select>
                            </div>
                          </div>

                    </div>     

                    <div className="form-group">
                        <label for="job_title">
                            Job Description <span className='required'>*</span> 
                        </label>
                        <textarea ref={register} className="form-control" id="job_description" refs="job_description" placeholder="Describe Roles & Responsibilies required for the job">
                        </textarea>
                    </div>             

                    <div className='sub-heading'> 
                        Targeting
                    </div>

                    <div className="form-group">
                      <div className="row">
                        <div className="col">
                            <label>
                                Category <span className='required'>*</span> 
                            </label>
                            <CategorySelection />
                        </div>
                        <div className="col">

                            <label>
                                Functional Area <span className='required'>*</span> 
                            </label>
                            <FunctionalAreaSelection />
                        </div>
                      </div>
                    </div>     

                    <div className="form-group">
                        <label>
                            Graduating Year <span className='required'>*</span> 
                        </label>
                          <div className="row">
                            <div className="col">
                                <select className="form-control">
                                    <option selected>Min Batch</option>
                                    {this.state.full_year_data.map(d => (<option value={d}>{d}</option>))}
                                </select>
                            </div>
                            <div className="col">
                                <select className="form-control">
                                    <option selected>Max Batch</option>
                                    {this.state.full_year_data.map(d => (<option value={d}>{d}</option>))}
                                </select>
                            </div>
                          </div>

                    </div>     


                    <div className="form-group">
                        <label for="job_locatiion">
                            Tags <span className='required'>*</span> 
                        </label>
                        <TagsInPut />
                    </div>



                    <button type="submit" className="btn btn-primary">Submit</button>

                </form>
            </div>  
        )
    //}
}

export default JobForm;
