import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import options from './locations_data';
import { Typeahead } from 'react-bootstrap-typeahead';

import './Jobpost.css';
import 'react-bootstrap-typeahead/css/Typeahead.css';

const LocationSelection = () => {
  const [selected, setSelected] = [];

  return (
    <Typeahead
      multiple  
      id="locations"
      onChange={setSelected}
      options={options}
      placeholder="Please add locations here..."
      selected={selected}
    />
  );
};

const YearSelection = () => {

    var array=[];

    for (var i = 1; i <=50; i++) {
        array.push(i);
    }

    var option_string = "";

    array.map(function(val){
      option_string += <option>{val}</option>;
    })

    return option_string;
};

class JobForm extends Component 
{
    constructor() 
    {
        super()
        this.state = 
        {
            data: {
                participants: ['Jake', 'Jon', 'Thruster']
            }
        }
    }

    getData(){
        axios.get('http://jsonplaceholder.typicode.com/users').then(res => {
          var data = res.data
            this.setState({data : data})
        })
    }

    componentDidMount(){
        this.setYearData()
    }


    render() {
        return(
            <div className='form-body'>
                <div className='form-heading'> Post Job </div>

                <form>

                    <div className='sub-heading'> 
                        Basic Details
                    </div>

                    <div className="form-group">
                        <label for="job_title">
                            Job Title <span className='required'>*</span> 
                        </label>
                        <input type="text" className="form-control" id="job_title" refs="job_title" placeholder="Write a title that appropriatly describes the job.">
                        </input>
                    </div>

                    <div className="form-group">
                        <label for="job_locatiion">
                            Locations <span className='required'>*</span> 
                        </label>
                        <LocationSelection />
                    </div>

                    <div className="form-group">
                        <label for="job_title">
                            Years of Experience <span className='required'>*</span> 
                        </label>
                          <div className="row">
                            <div className="col">
                                <select id="inputState" className="form-control">
                                    <option selected>Select Min</option>
                                    <option>...</option>
                                </select>
                            </div>
                            <div className="col">
                                <select id="inputState" className="form-control">
                                    <option selected>Select Max</option>
                                    <YearSelection />
                                </select>
                            </div>
                          </div>

                    </div>                    

                    <button type="submit" className="btn btn-primary">Submit</button>

                </form>
            </div>  
        )
    }
}

export default JobForm;
